// 1. Pega o número de erros. Usamos '|| 0' para garantir que temos um número.
const numErros = context.item.data?.['ultima_microprova_erradas'] || 0;

// 2. Usa um operador ternário para decidir a palavra
// (condição ? valor_se_verdadeiro : valor_se_falso)
const labelQuestao = numErros === 1 ? 'questão' : 'questões';

// 3. Monta a string final
return "Na microprova " +
    context.item.data?.['ultima_microprova_titulo'] +
    " você errou " +
    numErros +
    " " +
    labelQuestao;

