const microprovas = collections[/* VW historico_microprovas */ '221f8571-00fd-446a-8468-63189882881c']?.data || [];
const searchTerm = variables['3a0908d5-a1d9-4686-aab9-4f34c1af0bdf'] || '';
const sortOption = variables['ad1c8306-be49-45ea-b443-484683bfd9a1'] || 'Ordenar por: Mais recentes';

// Filter by search term
let filtered = microprovas.filter(microprova => {
  if (!searchTerm) return true;
  const searchLower = searchTerm.toLowerCase();
  const titulo = (microprova.microprova_titulo || '').toLowerCase();
  const disciplina = (microprova.disciplina_nome || '').toLowerCase();
  return titulo.includes(searchLower) || disciplina.includes(searchLower);
});

// Sort based on selected option
filtered.sort((a, b) => {
  switch (sortOption) {
    case 'Ordenar por: Mais antigas':
    case 'oldest':
      return new Date(a.data_resposta) - new Date(b.data_resposta);
    case 'Ordenar por: Melhor desempenho':
    case 'best':
      return b.percentual_acerto - a.percentual_acerto;
    case 'Ordenar por: Pior desempenho':
    case 'worst':
      return a.percentual_acerto - b.percentual_acerto;
    case 'Ordenar por: Mais recentes':
    case 'recent':
    default:
      return new Date(b.data_resposta) - new Date(a.data_resposta);
  }
});

return filtered;
