// Computed Variable: courseProgress
const disciplinas = collections['VW_disciplinas'].data || [];
const temas = collections['VW_temas_disciplina'].data || [];

// Agrupar temas por disciplina
const temasPorDisciplina = temas.reduce((acc, tema) => {
  if (!acc[tema.disciplina_id]) {
    acc[tema.disciplina_id] = [];
  }
  acc[tema.disciplina_id].push(tema);
  return acc;
}, {});

// Montar estrutura de módulos
const modules = disciplinas.map(disc => {
  const temasDisc = temasPorDisciplina[disc.disciplina_id] || [];
  const totalTemas = temasDisc.length;
  const temasDominados = temasDisc.filter(t => t.dominado).length;
  const progress = totalTemas > 0 ? Math.round((temasDominados / totalTemas) * 100) : 0;

  // Status
  let status = 'not-started';
  if (progress === 100) status = 'completed';
  else if (progress > 0) status = 'in-progress';

  return {
    id: disc.disciplina_id,
    title: disc.disciplina_nome,
    status: status,
    topics: temasDisc.map(tema => ({
      id: tema.tema,
      title: tema.tema,
      completed: tema.dominado
    })),
    progress: progress
  };
});

// Calcular progresso geral
const totalDisciplinas = disciplinas.length;
const disciplinasCompletas = modules.filter(m => m.status === 'completed').length;
const progressoGeral = totalDisciplinas > 0 
  ? Math.round((disciplinasCompletas / totalDisciplinas) * 100) 
  : 0;

return {
  modules: modules,
  overall: progressoGeral,
  totalModules: totalDisciplinas,
  modulesCompleted: disciplinasCompletas
};

