  // Pega o index da ALTERNATIVA
  const altIndex = context.item.index;

  // Converte para letra (0=A, 1=B, 2=C, 3=D, 4=E)
  const letra = String.fromCharCode(65 + altIndex);

  // Pega questões
  const qs = collections['30d603f0-555c-4c17-9ed3-29d2ace1a2b3']?.['data']?.[0]?.['questoes'] || []

  // ✅ TENTA PEGAR INDEX DA QUESTÃO DE VÁRIAS FORMAS
  let idx = context.item?.parent?.index;
  if (idx === undefined) idx = context.parentIndex;
  if (idx === undefined) idx = 0; // Fallback

  // Pega questão pelo index
  const q = Array.isArray(qs) && idx !== undefined ? qs[idx] : null;

  return {
    index: idx ?? 0,
    questaoId: q?.id || '',
    letra
  }
})()

