return (() => {
  // Pega resultado do Node 1
  const current = context.workflow['a58cff69-4bc8-4972-a580-180df1ed02c3'].result;

  // Pega array atual
  const list = Array.isArray(variables['b3c1af68-1d97-4d04-af52-9724af1a3b50']) 
    ? [...variables['b3c1af68-1d97-4d04-af52-9724af1a3b50']] 
    : [];

  // Valida se tem questão selecionada
  if (!current?.questaoId) return list;

  // Procura se já existe resposta para essa questão
  const i = list.findIndex(x => x.index === current.index);

  // Se existe, atualiza; senão, adiciona
  if (i >= 0) {
    list[i] = { ...list[i], ...current };
  } else {
    list.push(current);
  }

  console.log("Array atualizado:", list);

  return list;
})()

