return (() => {
    // Dados da microprova
    const microprova = collections['30d603f0-555c-4c17-9ed3-29d2ace1a2b3']?.['data']?.[0];

    // Respostas do aluno
    const respostasAluno = variables['b3c1af68-1d97-4d04-af52-9724af1a3b50'] || [];

    // Filtra temas das respostas erradas
    const temasErrados = respostasAluno
        .map(resposta => {
            const questao = microprova?.questoes?.find(q => q.id === resposta.questaoId);
            // Retorna tema apenas se a resposta está errada
            return questao && questao.gabarito?.toUpperCase() !== resposta.letra?.toUpperCase()
                ? questao.tema
                : null;
        })
        .filter(tema => tema !== null);

    // Remove duplicatas mantendo a ordem
    const temasUnicos = [...new Set(temasErrados)];

    console.log("Temas errados:", temasUnicos);

    return temasUnicos;
})()

