return (() => {
    const microprova = collections['30d603f0-555c-4c17-9ed3-29d2ace1a2b3']?.['data']?.[0];
    const respostasAluno = variables['b3c1af68-1d97-4d04-af52-9724af1a3b50'] || [];

    const correcao = microprova.questoes.map(questao => {
        const respostaAluno = respostasAluno.find(r => r.questaoId === questao.id);
        const letraAluno = respostaAluno?.letra || null;
        const alternativaAluno = letraAluno ? questao.alternativas[letraAluno.toLowerCase()] : null;
        const acertou = letraAluno?.toUpperCase() === questao.gabarito?.toUpperCase();

        return {
            correcao: {
                correcao: acertou,
                resposta: letraAluno,
                questaoId: questao.id,
                alternativa: alternativaAluno
            },
            turma_id: microprova.turma_id || null,
            disciplina_id: microprova.disciplina_id || null,
            microprova_titulo: microprova.titulo,
            questao_enunciado: questao.enunciado,
            tema: questao.tema,
            gabarito: questao.gabarito
        };
    });

    return correcao;
})()

