return (() => {
  // Pega respostas gravadas
  const respostas = variables['b3c1af68-1d97-4d04-af52-9724af1a3b50'] || [];

  // Pega questões originais
  const questoes = collections['30d603f0-555c-4c17-9ed3-29d2ace1a2b3']?.['data']?.[0]?.['questoes'] || [];

  // Monta array COM APENAS o que o aluno precisa ver
  return questoes.map((questao, idx) => {
    const resposta = respostas.find(r => r.index === idx);

    // Pega o texto da alternativa selecionada
    const alternativas = questao.alternativas || {};
    const letraSelecionada = resposta?.letra?.toLowerCase();
    const textoAlternativa = letraSelecionada ? alternativas[letraSelecionada] : "Não respondida";

    return {
      numero: idx + 1,
      letra: resposta?.letra || "—",
      alternativa: textoAlternativa
    };
  });
})()
