// Usa a collection pelo ID que você informou
const detalhe = collections['46c82fb9-0deb-438a-975a-4285daeb5d5a']?.data?.[0];
if (!detalhe) return [];

// questoes_json já é array de objetos (sem JSON.parse)
const questoes = Array.isArray(detalhe.questoes_json) ? detalhe.questoes_json : [];

// respostas_json é array de objetos {index, letra, questaoId}
// vamos indexar por questaoId para lookup O(1)
const respostasArray = Array.isArray(detalhe.respostas_json) ? detalhe.respostas_json : [];
const respostasPorQuestaoId = respostasArray.reduce((acc, r) => {
  if (r && r.questaoId) acc[r.questaoId] = r.letra;
  return acc;
}, {});

// helper: transforma alternativas-objeto em array de {letra, texto}
function normalizarAlternativas(obj) {
  if (!obj || typeof obj !== 'object') return [];
  const ordem = ['a','b','c','d','e'];
  return ordem
    .filter(k => obj[k] !== undefined && obj[k] !== null && String(obj[k]).trim() !== '')
    .map(k => ({ letra: k.toUpperCase(), texto: String(obj[k]) }));
}

// map final para a UI
return questoes.map((q, idx) => {
  const questaoId = q.id;
  const gabarito = (q.gabarito || '').toString().trim().toUpperCase(); // ex: 'B'
  const respostaAluno = (respostasPorQuestaoId[questaoId] || '').toString().trim().toUpperCase();
  const corretude = respostaAluno && gabarito && respostaAluno === gabarito;

  return {
    numero: idx + 1,
    id: questaoId,
    tema: q.tema || '',
    pergunta: q.enunciado || '',          // seu campo é 'enunciado'
    alternativas: normalizarAlternativas(q.alternativas), // [{letra:'A', texto:'...'}, ...]
    // Mantém compatibilidade com a UI existente:
    resposta_correta: gabarito,           // era resposta_correta; aqui é 'gabarito'
    resposta_aluno: respostaAluno || null,
    justificativa: q.justificativa || '', // se existir no seu JSON
    acertou: corretude,
    correta: corretude
  };
});
